import React from "react";
import styles from "./NewContent.module.css";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import Header from "../header/Header";
import axios from "axios";
import { useNavigate, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import { token } from "../Api";

const NewContent = (props) => {

  let navigate = useNavigate();

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [file, setFile] = useState("");
  const [imageFile, setImageFile] = useState("");
  const [imgUrl, setUrlImg] =useState([])
  const [fileUrl, setFileUrl] = useState('')
  const onChangeTitle = (event) => setTitle(event.target.value);
  const onChangeContent = (event) => setContent(event.target.value);

  console.log(fileUrl +','+ content)

  const str = 'https://blog-img-store2.s3.ap-northeast-2.amazonaws.com/';

  const submit = () => {
    var axios = require("axios");
    var data = JSON.stringify({
      content: fileUrl +','+ content,
      title: title,
    });

    var config = {
      method: "post",
      url: "http://localhost:8080/api/posts",
      headers: {
        "Content-Type": "application/json",
        Authorization: "Bearer " + token,
      },
      data: data,
    };

    axios(config)
      .then(function (response) {
        console.log(JSON.stringify(response.data));
        navigate("/");
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const onChange = (e) => {
    e.preventDefault();
    setFile(URL.createObjectURL(e.target.files[0]));

    const img = e.target.files[0];
    const formData = new FormData();
    formData.append("multipartFile", img);

    return axios
      .post("http://localhost:8080/api/img/s3/posts/upload", formData, {
        headers: {

          Authorization: "Bearer " + token,
        },
      })
      .then((res) => {
       console.log(res.data);
       const imageUrls = imgUrl.join()
        setUrlImg([...imgUrl, res.data]);
        console.log(imgUrl.join(' '))
        setFileUrl(imgUrl.join(' '))
        console.log(fileUrl)
      })
      .catch((err) => {
        alert("실패");
      });
  };

  return (
    <div className={styles.container}>
      <Header />
      <div className={styles.inputContainer}>
        <form>
          <input className={styles.inputTitle} type="text" placeholder="제목을 입력하세요"  onChange={onChangeTitle}/>     
          <textarea className={styles.inputContent} placeholder="내용을 입력하세요" onChange={onChangeContent}/>
          <button className={styles.contentButton} onClick={submit}>등록</button>
        </form>
      </div>
      <div>
        <input
          type="file"
          accept="image/jpg,image/png,image/jpeg,image/gif"
          name="profile_img"
          onChange={onChange}
        ></input>
      </div>
      <div className = {styles.image_container}>
        {
          imgUrl&&(
            imgUrl.map((url)=> <div className = {styles.image}><img src= {url} /></div>)
          )
        }
      </div>
      <button className={styles.imageButton} onClick={submit}>등록</button>
    </div>
  );
};

export default NewContent;
