import { useParams, useLocation } from "react-router";
import axios from "axios";
import { useState, useEffect, useRef } from "react";
import Header from "./header/Header";
import styles from "./PostDetail.module.css";
import { Link, useNavigate } from "react-router-dom";

function PostDetail(props) {
  const { postId } = useParams();
  const { state } = useLocation();
  const navigate = useNavigate();

  const onDelete = (event) => {
    event.preventDefault();

    axios
      .delete(`http://localhost:8080/api/posts/${postId}`)
      .then((response) => {
        // response
        console.log("content:", response);
      })
      .catch((error) => {
        // 오류발생시 실행
      })
      .then(() => {
        // 항상 실행
        navigate("/");
      });
    //원래 페이지로 돌아가기
  };

  const [title, setTitle] = useState();
  const [content, setContent] = useState();
  const [comments, setComments] = useState({
    replyId: 0, content: 'dfas'
  });

  const textRef = useRef();

  useEffect(() => {
    axios
      .get(`http://localhost:8080/api/posts/${postId}`)
      .then((response) => {
        // response
        // let result = response.data.replyDtoList.map(a => a.content);
      //  console.log(response.data.replyDtoList)
      //   setComments(...response.data.replyDtoList)
      //    console.log(comments)

        setTitle(response.data.title);
        setContent(response.data.content);
      })
      .catch((error) => {
        // 오류발생시 실행
      })
  }, []);


  const replySubmit = () =>{
    let text = textRef.current.value;

    axios.post('http://localhost:8080/api/reply', {
        "content": text,
        "postsId": postId
    }).then(res =>{
      // console.log('63', res.data.replyDtoList)
      // const result = [...res.data.replyDtoList]
      // setComments(result)
      // console.log(comments)
    }).then(() =>{
      axios
      .get(`http://localhost:8080/api/posts/${postId}`)
      .then((response) => {
        console.log(response.data.replyDtoList)
        setComments([comments, ...response.data.replyDtoList])
        console.log('comments', comments)
      })
    })


  }

  return (
    <div>
      <div>
        <Header></Header>
      </div>
      <div className={styles.postContainer}>
        <div className={styles.postTitle}>{title}</div>
        <div className={styles.postContent}>{content}</div>

        <div>
          <input 
           ref = {textRef} 
           type = "text"
           onKeyPress = {(e) =>{
             if(e.key == "Enter"){
               replySubmit();
               textRef.current.value = "";
              }
           }
           }
           />
          <button onClick= {replySubmit}>submit</button>
        </div>


{/*           
            <ul>
            {
              comments.map(item =>(
                <li key = {item}>{item}</li>
              ))
            } 
            </ul>
          */}

        <div className={styles.BtnContainer}>
          <Link
            to={"/modify"}
            state={{
              title: title,
              content: content,
              postId: postId,
            }}
          >
            <button className={styles.Btn}>수정</button>
          </Link>
          <button className={styles.Btn} onClick={onDelete}>
            삭제
          </button>
        </div>
      </div>
    </div>
  );
}

export default PostDetail;
